var moment = global.get('moment');
function checkSensorExist(checkSensor, currentSensors) {
    var isExist = false;
    for (var i = 0; i < currentSensors.length; i++) {
        if (checkSensor.name == currentSensors[i].name) {
            isExist = true;
            break;
        }
    }
    return isExist;
}

function createQueryaddNewSensors(newSensors, macAddr) {
    var addSensorsQuery = '';
    var bindQuery = [];
    for (var i = 0; i < newSensors.length; i++) {
        var newSensor = newSensors[i];
        addSensorsQuery += 'INSERT INTO sensor VALUES (?, ?, ?,?,?); ';
        bindQuery.push(
            newSensor.name,
            macAddr,
            newSensor.unit, 
            'ONLINE',
            moment(new Date()).format("YYYY-MM-DD HH:mm:ss")
            );
    }
    return {
        query: addSensorsQuery,
        bindQuery: bindQuery
    };
}

var currentSensors = msg.sensorsQueryResult;
var deviceSensors = msg.payload.sensors;
var macAddr = msg.payload.macAddr;
var newSensors = [];
for (var i = 0; i < deviceSensors.length; i++) {
    checkDeviceSensor = deviceSensors[i];
    if (checkSensorExist(checkDeviceSensor, currentSensors) === false) {
        newSensors.push(checkDeviceSensor);
    }
}

if (newSensors.length > 0) {
    var addNewSensorsQuery = createQueryaddNewSensors(newSensors, macAddr);
    var newMsg = {
        query:addNewSensorsQuery.query,
        bindQuery:addNewSensorsQuery.bindQuery,
        payload: msg.payload
    };
    return [newMsg, null];
} else {
    return [null, msg];
}

