//	C: [1, 100]; 	%: [0, 100],	lux: [1, 65535]
let macAddr = msg.payload.macAddr;
let sensorData = msg.payload.sensorsData;
let serieDataList=[];
for (var i = 0; i < sensorData.length; i++) {
    let checkSensorData = sensorData[i];
    serieDataList.push({
        tags: {
            'macAddr': macAddr,
            'name': checkSensorData.name,
        },
        fields: {
            'unit': checkSensorData.unit,
            'value': checkSensorData.value
        },
        time_stamp: msg.receivedTime.getTime() * Math.pow(10, 6)
    });
}
let sendMsg = msg;
if (serieDataList.length > 0) {
    sendMsg.serieDataList = serieDataList;
    node.send(sendMsg);
}

msg.payload = JSON.parse(msg.payload);
msg.receivedTime = new Date();
